import { View } from "react-native";
import React, { useState } from "react";
import { Card, Text, Button } from "react-native-paper";

export default function Pessoa() {
  const [pessoa, setPessoa] = useState({});

  function revelar() {
    const novaPessoa = {
      nome: "Lucy",
      idade: "20",
      imagem:
        "data:image/jpeg;base64,",
    };
    setPessoa(novaPessoa);
  }
  function revelar2() {
    const novaPessoa = {
      nome: "Rabixo",
      idade: "38",
      imagem:
        "data:image/jpeg;base64,",
    };
    setPessoa(novaPessoa);
  }
  function revelar3() {
    const novaPessoa = {
      nome: "Almofadinhas",
      idade: "36",
      imagem:
        "data:image/jpeg;base64,",
    };
    setPessoa(novaPessoa);
  }

  return (
    <View>
      <Card>
        <Card.Content>
          <Text variant="displaySmall">Componente Pessoa</Text>
          <Text variant="displaySmall">Nome: {pessoa.nome}</Text>
          <Text variant="displaySmall">Idade: {pessoa.idade}</Text>
          <Card.Cover source={{ uri: pessoa.imagem }} style={{ margin: 10 }} />
        </Card.Content>
        <Card.Actions>
          <Button onPress={revelar}>Revelar</Button>
          <Button onPress={revelar2}>Revelar 2</Button>
          <Button onPress={revelar3}>Revelar 3</Button>
        </Card.Actions>
      </Card>
    </View>
  );
}
